package LoopsEx;
public class TablesTwotoTen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=2;
		do
		{
			for(int i=1;i<=10;i++)
			{
				System.out.println(n+"x"+i+"="+(n*i));
			}
			n++;
			System.out.println();
		}while(n<=10);
		
			

	}

}

